create table fields
(
    id             int unsigned auto_increment
        primary key,
    tank_id        int unsigned  not null,
    level          int           null,
    `left-name`    varchar(255)  null,
    `left-image`   varchar(255)  null,
    `left-active`  int default 0 null,
    `right-name`   varchar(255)  null,
    `right-image`  varchar(255)  null,
    `right-active` int default 0 null,
    constraint fields_tanks_id_fk
        foreign key (tank_id) references tanks (id)
            on update cascade
);

